"use strict";

var body = document.querySelector("body"),
    burgerMenu = document.querySelector(".burger"),
    headerNav = document.querySelector(".nav"),
    header = document.querySelector(".header");

function stickyHeader() {
  var e = header.nextElementSibling;
  header.classList.add("fixedToTop"), e.style.marginTop = header.offsetHeight + "px", e.style.minHeight = "90vh";
}

burgerMenu.addEventListener("click", function (e) {
  e.preventDefault(), burgerMenu.classList.toggle("burger--active"), body.classList.toggle("body-lock"), headerNav.classList.toggle("nav--active"), header.classList.toggle("header--active");
}), stickyHeader();